INSERT INTO `main_ori` VALUES ('NCCN Guideline: Non-Small Cell Lung Cancer (NSCLC) <2.2020>', '.', 'guideline:NCCN', '1', 'erlotinib', 'erlotinib', 'erlotinib', '厄洛替尼', 'erlotinib|厄洛替尼', '4', 'non-small cell lung cancer', 'non-small cell lung cancer', '肺癌', 'na', 'resistance', 'EVI_000026', 'EGFR:nonsynonymous SNV:exon20:p.T790M:.', 'EGFR', 'nonsynonymous SNV', 'Exact_pos', 'exon20', 'p.T790M', '.', 'Acquired resistance (to EGFR TKI therapy) is associated with second site mutations within the  EGFR kinase domain, amplification of alternative kinases (such as MET), histologic transformation  from NSCLC to SCLC, and epithelial to mesenchymal transition (EMT).', '患者对EGFR TKI治疗的获得性耐药可能是由于EGFR基因的二次突变(如发生在激酶结构域的T790M)、MET基因扩增、组织学变化(非小细胞肺癌变成小细胞肺癌)或者上皮细胞向间充质细胞转换(EMT)等情况引起。', 'S', '耐药', 'o');
