INSERT INTO `main_ori` VALUES ('PMID:24265155', '.', 'preclinical', '4', 'vemurafenib', 'vemurafenib', 'vemurafenib', '维莫非尼', 'vemurafenib|维莫非尼', '4', 'melanoma', 'melanoma', '肿瘤细胞', 'na', 'resistance', 'EVI_000148', 'AKT1:nonsynonymous SNV:exon4:p.Q79K:.', 'AKT1', 'nonsynonymous SNV', 'Exact_pos', 'exon4', 'p.Q79K', '.', 'The presence of AKT1 activating mutations alters BRAF inhibitor sensitivity. Compared to AKT1 wild type melanoma cells, AKT1 Q79K or E17K conferred vemurafenib resistance. The combination of vemurafenib and MK2206 was more effective than either agent alone in reducing melanoma cell growth expressing AKT1 Q79K.', 'AKT1基因的Q79K或E17K突变可能与肿瘤对维莫非尼的耐药相关。', 'S', '可能耐药', 'o');
