INSERT INTO `main_ori` VALUES ('PMID:20179196', '.', 'preclinical', '4', '[endocrine therapy]', '[endocrine therapy]', '[Endocrine therapy]', '【激素治疗】', '[Endocrine therapy]|【激素治疗】', '5', 'cancer cell lines', 'cancer cell lines_', '肿瘤细胞', 'na', 'resistance', 'EVI_000383', 'FGFR1:CNV:.:.:gain', 'FGFR1', 'CNV', 'CNV', '.', '.', 'gain', 'amplification and overexpression of FGFR1 may be a major contributor to poor prognosis in luminal-type breast cancers, driving anchorage-independent proliferation and endocrine therapy resistance. ', 'FGFR1基因的扩增或者过表达与luminal型乳腺癌预后不良有关，可能导致肿瘤非依赖性生长和对内分泌治疗的耐药。', 'A', '可能耐药', 'o');
