INSERT INTO `main_ori` VALUES ('Annals of Oncology (2016) 27 (6): 416-454. 10.1093/annonc/mdw383', 'NCT01877083', 'clinical trial - phase2', '4', 'lenvatinib', 'lenvatinib', 'lenvatinib', '仑伐替尼', 'lenvatinib|仑伐替尼', '4', 'non-small cell lung cancer', 'non-small cell lung cancer', '肺癌', 'na', 'sensitivity', 'EVI_000394', 'RET:FUS:.:.-RET:.', 'RET', 'FUS', 'FUS', '.', '.-RET', '.', 'LN showed promising clinical activity in pts with RET-positive NSCLC. For most pts, toxicities were manageable with dose modification. These results provide support for LN as a potential treatment for RET-positive NSCLC.', '一项临床试验表明，携带RET阳性的肿瘤患者可能对lenvatinib敏感。', 'F', '可能敏感', 'o');
INSERT INTO `main_ori` VALUES ('PMID:23856031', '.', 'preclinical', '4', 'lenvatinib', 'lenvatinib', 'lenvatinib', '仑伐替尼', 'lenvatinib|仑伐替尼', '4', 'cancer cell lines', 'cancer cell lines_', '肿瘤细胞', 'na', 'sensitivity', 'EVI_000395', 'RET:FUS:.:KIF5B-RET:.', 'RET', 'FUS', 'FUS', '.', 'KIF5B-RET', '.', 'Lenvatinib suppressed the growth of CCDC6-RET human thyroid and lung cancer cell lines, and as well, suppressed anchorage-independent growth and tumorigenicity of RET gene fusion-transformed NIH3T3 cells. These results demonstrate that lenvatinib can exert antitumor activity against RET gene fusion-driven tumor models by inhibiting oncogenic RET gene fusion signaling.', 'Lenvatinib对携带RET融合基因的肿瘤细胞有抑制作用。', 'F', '可能敏感', 'o');
INSERT INTO `main_ori` VALUES ('ASCO 2015 (abstr e15516)', 'NCT02122172', 'clinical trial - phase2', '4', 'afatinib   ', 'afatinib', 'afatinib', '阿法替尼', 'afatinib|阿法替尼', '4', 'urothelial carcinoma', 'bladder cancer', '膀胱癌', 'na', 'sensitivity', 'EVI_000396', 'ERBB3:nonsynonymous SNV:exon7:p.G284R:.', 'ERBB3', 'nonsynonymous SNV', 'Exact_pos', 'exon7', 'p.G284R', '.', '.', '铂类药物耐药的泌尿道上皮癌患者常发生EGFR (ErbB1), HER2 (ErbB2), 和 ErbB3 拷贝数的变化。阿法替尼是一种Erbb蛋白家族抑制剂。一项临床Ⅱ期实验中对铂类耐药的泌尿道上皮癌患者采用阿法替尼治疗。14名患者中3名获得缓解，其中1名患者携带4个拷贝的HER2，获得部分缓解，且缓解时间为6.9个月；一名患者ErbB3 G284R突变，缓解时间为7个月；1名患者HER2扩拷贝数扩增至73个，且ErbB3 R103G突变，无进展生存期长达10.3个月。所以HER2拷贝数增加或携带ERBB3突变的上皮癌患者可以从阿法替尼治疗中获益。', 'S', '可能敏感', 'o');
