INSERT INTO `main_ori` VALUES ('Druglabel:lumacaftor_Revised:05/2016', '.', 'guideline:FDA', '1', 'lumacaftor', 'lumacaftor', 'lumacaftor', 'lumacaftor', 'lumacaftor|lumacaftor', '4', 'cystic fibrosis', 'cystic fibrosis', '非特定癌症', 'na', 'sensitivity', 'EVI_000470', 'CFTR:nonframeshift deletion:exon11:p.F508del:.', 'CFTR', 'nonframeshift deletion', 'Exact_pos', 'exon11', 'p.F508del', '.', '.', 'Lumacaftor与ivacaftor联合用于治疗12岁以上囊性纤维化，患者为CFTR F508del突变纯合子型。局限性：lumacaftor治疗非CFTR F508del突变纯合子型患者的有效性和安全性尚未建立。Orkambi（lumacaftor 200 mg/ivacaftor 125 mg）现已批准用于治疗12岁及以上患有F508del突变的患者的囊性纤维化（CF），该突变导致产生异常蛋白质，从而破坏水和氯化物的运输方式身体。有两个拷贝的这个突变（一个从每个父母继承的）是CF的主要原因。在2108名具有F508del突变的12岁以上CF患者，研究了Orkambi的安全性和有效性。CF患者服用Orkambi的患者，每12小时服用两片药物，与服用安慰剂的患者相比，肺功能改善。除了具有F508del突变的患者，Orkambi的疗效和安全性尚未确定。如果患者的基因型未知，则应使用FDA批准的CF突变检测来检测CFTR基因的两个等位基因上F508del突变的存在。', 'S', '敏感', 'o');
