INSERT INTO `main_ori` VALUES ('PMID:25688157', '.', 'preclinical', '4', 'crizotinib', 'crizotinib', 'crizotinib', '克唑替尼', 'crizotinib|克唑替尼', '4', 'cancer cell lines_lung', 'cancer cell lines_lung', '肿瘤细胞', '肿瘤细胞', 'resistance', 'EVI_000509', 'ROS1:nonsynonymous SNV:exon41:p.L2155S:.', 'ROS1', 'nonsynonymous SNV', 'Exact_pos', 'exon41', 'p.L2155S', '.', 'The ROS1 G2032R mutation was identified in crizotinib-resistant tumors from one patient. Furthermore, HCC78CR1 and CR2 cells harbored a novel ROS1 L2155S mutation (73.3% and 76.2%, respectively). ROS1 G2032R and L2155S mutations conferred resistance to crizotinib in Ba/F3 cells.', '携带ROS1基因融合的肿瘤对crizotinib治疗敏感，但ROS1基因上的点突变L2155S会阻碍抑制剂与融合蛋白的结合，从而导致crizotinib耐药。', 'S', '可能耐药', 'o');
