INSERT INTO `main_ori` VALUES ('PMID:18794099', '.', 'clinical study', '4', 'cetuximab', 'cetuximab', 'cetuximab', '西妥昔单抗', 'cetuximab|西妥昔单抗', '4', 'colorectal cancer', 'colorectal cancer', '结直肠癌', 'na', 'sensitivity', 'EVI_000582', 'EGFR:CNV:.:.:gain', 'EGFR', 'CNV', 'CNV', '.', '.', 'gain', 'One study confirmed the association between increased EGFR GCN and outcome after cetuximab. However, because of reproducibility concerns, any decision making based on published cutoff points is not warranted.(PMID:18794099)', '一项临床试验表明，在结直肠癌患者中EGFR基因扩增可能对西妥昔单抗敏感。', 'A', '可能敏感', 'o');
