INSERT INTO `main_ori` VALUES ('NCCN Guideline: Non-Small Cell Lung Cancer (NSCLC) <2.2020>|PMID:24588695', '.', 'guideline:NCCN', '1', 'icotinib', 'icotinib', 'icotinib', '埃克替尼', 'icotinib|埃克替尼', '4', 'non-small cell lung cancer', 'non-small cell lung cancer', '肺癌', '非小细胞肺癌', 'resistance', 'EVI_000656', 'KRAS:nonsynonymous SNV:.:.:act', 'KRAS', 'nonsynonymous SNV', 'None_pos', '.', '.', 'act', '.', 'KRAS和NRAS基因exon2(codons 12 and 13),3(codons 59 and 61),以及4(codons 117 and 146)位点的突变会导致RAS信号通路的异常活化(PMID:20570890)。RAS基因是EGFR通路下游的效应因子，突变型RAS基因编码异常的蛋白会促进肿瘤细胞的生长和扩散，且不受上游EGFR信号影响。携带这些基因突变的肿瘤患者可能对EGFR信号通路抑制剂类靶向药物耐药。NCCN指南建议KRAS基因突变与肿瘤患者对EGFR抑制剂的原发耐药有关。', 'S', '耐药', 'o');
