INSERT INTO `main_ori` VALUES ('PMID:18612151', '.', 'clinical trial - phase2', '4', 'cetuximab', 'cetuximab', 'cetuximab', '西妥昔单抗', 'cetuximab|西妥昔单抗', '4', 'non-small cell lung cancer', 'non-small cell lung cancer', '肺癌', 'na', 'sensitivity', 'EVI_000803', 'EGFR:CNV:.:.:gain', 'EGFR', 'CNV', 'CNV', '.', '.', 'gain', '.', '一项临床II期研究，共入组了229名晚期非小细胞肺癌患者，130名腺癌患者，42名鳞状细胞癌患者，57名其他亚型的肺癌患者。使用原位免疫荧光杂交试验（FISH）诊断出76名患者为EGFR基因扩增阳性，FISH阳性的判读标准为40%的癌细胞有4个以上的基因拷贝数。试验入组的患者随机分为两组：一组患者同时使用西妥昔单抗+化疗（紫杉醇联合卡铂）；另一组患者先使用化疗（紫杉醇联合卡铂），进展后再次使用西妥昔单抗。FISH检测EGFR基因扩增阳性患者，化疗联合西妥昔单抗使得患者的无进展生存期（PFS）和总生存期（OS）都明显好于EGFR基因扩增阴性患者。具体的数据如下：治疗应答率（包括完全应答和部分应答），FISH检测EGFR基因扩增阳性的为45%，而对照未扩增组的治疗应答率为26%。疾病控制率（包括完全应答、部分应答和病情稳定），EGFR基因扩增阳性组为81%，而未扩增组的疾病控制率为55%。中位无进展生存期，EGFR基因扩增阳性组为6个月，而未扩增组的疾病控制率为3个月。中位总生存期，EGFR基因扩增阳性组为15个月，而未扩增组的疾病控制率为7个月。', 'A', '可能敏感', 'o');
