INSERT INTO `main_ori` VALUES ('NCT03218826', 'NCT03218826', 'clinical trial - phase1', '4', 'AZD8186', 'AZD8186', 'AZD8186', 'AZD8186', 'AZD8186|AZD8186', '1', 'solid tumor', 'solid tumor', '非特定癌症', 'na', 'sensitivity', 'EVI_000820', 'PTEN:nonsynonymous SNV:.:.:inact', 'PTEN', 'nonsynonymous SNV', 'None_pos', '.', '.', 'inact', '.', 'I期临床试验研究了PI3Kbeta抑制剂AZD8186与多西紫杉醇联合可以用于治疗PTEN或PIK3CB突变的实体瘤患者。', 's', '可能敏感', 'o');
