INSERT INTO `main_ori` VALUES ('PMID:27401242', '.', 'case report', '4', 'crizotinib', 'crizotinib', 'crizotinib', '克唑替尼', 'crizotinib|克唑替尼', '4', 'non-small cell lung cancer', 'non-small cell lung cancer', '肺癌', '[ROS:FUS]', 'resistance', 'EVI_001347', 'ROS1:nonsynonymous SNV::p.S1986Y:', 'ROS1', 'nonsynonymous SNV', 'Exact_pos', '.', 'p.S1986Y', '.', '.', '一名携带EZR-ROS1融合基因的转移性NSCLC的患者对ALK/ROS1抑制剂crizotinib产生获得性耐药。基因测序发现在其ROS1激酶结构域中发生了双重突变，S1986Y和S1986F。体外功能性研究证明ROS1含有S1986Y或S1986F突变时可以导致肿瘤细胞对crizotinib或ceritinib耐药，但对lorlatinib（PF-06463922）抑制敏感。', 's', '可能耐药', 'o');
INSERT INTO `main_ori` VALUES ('PMID:27401242', '.', 'case report', '4', 'crizotinib', 'crizotinib', 'crizotinib', '克唑替尼', 'crizotinib|克唑替尼', '4', 'non-small cell lung cancer', 'non-small cell lung cancer', '肺癌', '[ROS:FUS]', 'resistance', 'EVI_001348', 'ROS1:nonsynonymous SNV::p.S1986F:', 'ROS1', 'nonsynonymous SNV', 'Exact_pos', '.', 'p.S1986F', '.', '.', '一名携带EZR-ROS1融合基因的转移性NSCLC的患者对ALK/ROS1抑制剂crizotinib产生获得性耐药。基因测序发现在其ROS1激酶结构域中发生了双重突变，S1986Y和S1986F。体外功能性研究证明ROS1含有S1986Y或S1986F突变时可以导致肿瘤细胞对crizotinib或ceritinib耐药，但对lorlatinib（PF-06463922）抑制敏感。', 's', '可能耐药', 'o');
INSERT INTO `main_ori` VALUES ('AACR 2019 (abstr CT234)', 'NCT03140670', 'clinical trial - phase2', '4', 'rucaparib', 'rucaparib', 'rucaparib', '芦卡帕利', 'rucaparib|芦卡帕利', '4', 'pancreatic cancer', 'pancreatic cancer', '胰腺癌', 'na', 'sensitivity', 'EVI_001349', 'BRCA1:nonsynonymous SNV:.:.:inact', 'BRCA1', 'nonsynonymous SNV', 'None_pos', '.', '.', 'inact', '.', '一项正在进行的单臂II期研究正在评估Rubrac(600mg，每日2次)用于一线维持治疗的疗效和安全性。该研究将入组42例晚期胰腺癌患者，这些患者肿瘤携带致病性的种系或体细胞BRCA1、BRCA2或PALB2突变，接受了至少4个月的含铂化疗后病情没有进展。研究的主要终点是无进展生存期(PFS)，实体瘤疗效评价标准(RECIST v1.1)评价。在中期分析中，19例可评估患者的中位PFS为278天或9.1个月(从Rubraca治疗开始)。在中位随访244天时，中位总生存期(OS)未达到。在最后一次数据截止时评估的19名患者中，1例患者实现完全缓解(CR)，另外6例患者实现部分缓解(PR)，包括种系BRCA2突变(n=4)、种系PALB2突变(n=2)和体细胞BRCA2突变(n=1)患者。19例患者中有8例接受Rubraca治疗了6个月以上，2例治疗了1年以上(13个月和15个月)。第8周随访的疾病控制率(定义为CR+PR+稳定疾病)为89.5%。总的来说，本研究中的Rubraca治疗耐受性良好，无剂量限制性毒性。', 'G;S', '可能敏感', 'o');
