INSERT INTO `main_ori` VALUES ('J Clin Oncol 37, 2019 (suppl; abstr 5005)', 'NCT01682772', 'clinical trial - phase2', '4', 'olaparib', 'olaparib', 'olaparib', '奥拉帕利', 'olaparib|奥拉帕利', '4', 'prostate cancer', 'prostate cancer', '前列腺癌', 'na', 'sensitivity', 'EVI_001414', 'BRCA1:nonsynonymous SNV:.:.:inact', 'BRCA1', 'nonsynonymous SNV', 'None_pos', '.', '.', 'inact', '.', 'TOPARP研究是一项评估奥拉帕利对转移性去势抵抗前列腺癌（mCRPC）患者疗效的II期临床试验，在之前TOPARP-A实验中，研究了奥拉帕利在未经分子标记物筛选的转移性去势抵抗性前列腺癌（mCRPC）患者中的抗肿瘤活性，总体反应性为33%，但DNA修复相关基因（DRG）存在异常的患者对奥拉帕利响应率达到88%，研究者针对这一发现进行了进一步的TOPARP-B研究。TOPARP-B评估了奥拉帕利对紫杉醇化疗≥1次后靶向测序DDR基因改变（种系或体细胞；单或双等位基因）的患者的疗效，最终92例患者纳入终点分析，结果显示：总体人群的综合反应率（composite response rate）为46.7%，亚组分析显示：BRCA1/2突变亚组的综合反应率达到83.3%。ATM突变组综合反应率为36.8%，CDK12突变组综合反应率为25.0%，PALB2突变组的综合反应率为57.1%，其他组的综合反应率为20.0%。', 'G;S', '可能敏感', 'n');
