INSERT INTO `main_ori` VALUES ('PMID:27785061', '.', 'clinical study', '4', 'gefitinib', 'gefitinib', 'gefitinib', '吉非替尼', 'gefitinib|吉非替尼', '4', 'non-small cell lung cancer', 'non-small cell lung cancer', '肺癌', 'na', 'resistance', 'EVI_001426', 'EGFR:nonsynonymous SNV:exon18:p.E709_T710delinsD:', 'EGFR', 'nonsynonymous SNV', 'Exact_pos', 'exon18', 'p.E709_T710delinsD', '.', '.', '一项回顾性临床研究发现，在3146名患者中，有1689名（53.7%）患有EGFR突变。其中包括726例外显子19缺失的患者，733例L858R患者和230例（13.6%）其他EGFR突变患者。在具有其他突变的230名患者中，25名（1.5%）患者具有罕见的E709X突变。 20名患者具有复杂的E709X突变(同时含有其它EGFR激活突变），5名具有单个E709X突变：delE709-T710insD（不携带其他EGFR激活突变）。在这25名患者中，有18名患者接受了吉非替尼或厄洛替尼治疗。 TKI治疗的有效率为50.0%，中位无进展生存期为6.2个月。所有5名delE709-T710insD患者对TKI治疗无反应。', 's', '可能耐药', 'n');
