INSERT INTO `main_ori` VALUES ('Annals of Oncology (2018) 29 (suppl_8): viii493-viii547. 10.1093/annonc/mdy292', 'NCT01982955', 'clinical trial - phase2', '4', 'tepotinib', 'tepotinib', 'tepotinib', 'tepotinib', 'tepotinib|tepotinib', '2', 'NSCLC', 'NSCLC', '肺癌', 'na', 'sensitivity', 'EVI_001454', 'MET:CNV:.:.:gain', 'MET', 'CNV', 'CNV', '.', '.', 'gain', '.', 'NSCLC可通过MET激活获得对EGFR酪氨酸激酶抑制剂（EGFR TKIs）的抗性；MET/EGFR双重抑制可能在对EGFR TKI耐药的NSCLC中具有一定疗效。tepotinib是一种有效的选择性MET抑制剂。我们报告[tepotinib + gefitinib]与化疗[培美曲塞+顺铂/卡铂]在MET+、EGFR+、T790M-、NSCLC患者中的疗效（NCT01982955）。具有高度MET +（IHC2 +，IHC3 +，基因扩增）的亚洲NSCLC患者，对一线EGFR TKI的耐药。肿瘤具有EGFR活化突变（T790M-）。患者每天一次接受tepotinib + gefitinib 500 /250mg。主要终点：无进展生存期（PFS）。次要终点：安全性，抗肿瘤活性，药代动力学。结果由于招募率低，55名患者被随机分配到tepotinib + gefitinib（n = 31）或化疗（n = 24）后停止入组。MET免疫组化IHC 3+的患者中位无进展生存期为8.3个月；MET基因扩增（GCN ≥5 or MET/CEP7 ratio ≥2/EGFR T790M-)的患者中位无进展生存期达到21.2个月。', 'A', '可能敏感', 'n');
