INSERT INTO `main_ori` VALUES ('NCCN Guideline: Colon Cancer <2.2019>', '.', 'guideline:NCCN', '1', '.', '.', 'irinotecan + cetuximab + vemurafenib', '伊立替康 + 西妥昔单抗 + 维莫非尼', 'irinotecan + cetuximab + vemurafenib|伊立替康 + 西妥昔单抗 + 维莫非尼', '4', 'Colon Cancer', 'Colon Cancer', '结直肠癌', 'na', 'sensitivity', 'EVI_001459', 'BRAF:nonsynonymous SNV:exon15:p.V600.:gain', 'BRAF', 'nonsynonymous SNV', 'Partial_pos', 'exon15', 'p.V600.', '.', '.', 'NCCN指南中推荐BRAF V600E突变的转移性肠癌患者可以使用irinotecan + cetuximab + vemurafenib联合治疗。SWOG S1406临床试验表明，99例BRAF V600E突变但RAS野生型的mCRC患者，接受过至少一线治疗失败（不含EGFR单抗）后，随机接受伊立替康+西妥昔单抗加或不加BRAF抑制剂维莫非尼，结果显示主要终点PFS（无进展生存期）从2个月显著延长至4.4个月，次要终点ORR（客观缓解率）也显著改善（4%提高到16%）。', 's', '敏感', 'n');
