INSERT INTO `main_ori` VALUES ('PMID:28242752', 'NCT01286987', 'clinical trial - phase1', '4', 'talazoparib', 'talazoparib', 'talazoparib', 'talazoparib', 'talazoparib|talazoparib', '4', '.', '.', '卵巢癌', 'na', 'sensitivity', 'EVI_001469', 'BRCA2:.:.:.:inact', 'BRCA2', 'nonsynonymous SNV', 'None_pos', '.', '.', 'inact', '.', 'Talazoparib是一种PARP抑制剂，可以导致携带BRCA1/2有害突变的细胞死亡。一项开放标签，多中心，剂量递增的（NCT01286987）临床试验研究了Talazoparib在多个实体瘤中的抗肿瘤活性，MTD，药代动力学和药效学。在12例携带BRCA1/2有害突变的卵巢癌的患者中，Talazoparib 1.0 mg /天治疗后，ORR和CBR分别为42%和67%，中位PFS为36.4周。临床试验中的13例胰腺癌患者，4例具有临床获益（CBR，31%≥，16周）：2例患者部分缓解，1例携带BRCA2突变，另1例携带PALB2突变。', 's', '可能敏感', 'n');
