INSERT INTO `main_ori` VALUES ('PMID:29182496', 'NCT02150967', 'clinical trial - phase2', '4', 'BGJ398', 'BGJ398', 'infigratinib', 'infigratinib', 'infigratinib|infigratinib', '2', 'Cholangiocarcinoma', 'Cholangiocarcinoma', '胆管癌', 'na', 'sensitivity', 'EVI_001524', 'FGFR2:FUS:.:FGFR2-.:.', 'FGFR2', 'FUS', 'FUS', '.', 'FGFR2-.', '.', '.', '一项多中心，开放标签的II期临床试验（NCT02150967）评估了BGJ398（infigratinib）对≥18岁、晚期或转移性胆管癌患者的疗效。这些胆管癌患者携带FGFR2融合或其他FGFR改变。患者每天一次接受BGJ398 125mg，持续21天，然后休息7天（28天循环）。主要终点总体反应率。携带FGFR2融合（n = 48）、突变（n = 8）或扩增（n = 3）的61名患者（35名女性；中位年龄57岁）。在预先指定的数据截止日期（2016年6月30日），50名患者停止治疗。总反应率为14.8%（FGFR2融合类患者为18.8%），疾病控制率为75.4%（FGFR2融合类患者为83.3%），估计中位无进展生存期为5.8个月（95%CI，4.3至7.6个月）。', 'F', '可能敏感', 'n');
