INSERT INTO `main_ori` VALUES ('PMID:24024839|PMID:27843597|NCCN Guideline: Colon Cancer <2.2019>', 'NCT00364013', 'guideline:NCCN', '1', 'FOLFOX + panitumumab', 'FOLFOX + panitumumab', 'FOLFOX + panitumumab', 'FOLFOX + 帕尼单抗', 'FOLFOX + panitumumab|FOLFOX + 帕尼单抗', '4', 'metastatic adenocarcinoma of the colon or rectum', ' metastatic colorectal cancer ', '结直肠癌', 'na', 'resistance', 'EVI_001537', 'KRAS:nonsynonymous SNV:.:.:act', 'KRAS', 'nonsynonymous SNV', 'None_pos', '.', '.', 'act', '.', '一项III期临床试验中，512例无RAS突变的患者中，帕尼单抗-FOLFOX4的无进展生存期为10.1个月，单独使用FOLFOX4为7.9个月。帕尼单抗-FOLFOX4组总生存期为26.0个月，单独FOLFOX4组为20.2个月。 共有108名患者（17%）无KRAS exon2突变，但携带其他RAS基因激活突变。携带这些突变的患者，帕尼单抗-FOLFOX4联合治疗后患者的无进展生存期和总生存率低于野生型患者。在一项正对1183例转移性结直肠癌患者的随机临床III期试验中，患者接受帕尼单抗联合FOLFOX或单独FOLFOX治疗。经过治疗后，在携带KRAS突变的患者中，与FOLFOX组患者相比，帕尼单抗联合FOLFOX组患者具有缩短的总生存期（15 vs 19.2个月，P=0.14）以及无进展生存期（7.4 vs 9.2个月，P=0.02）。', 's', '耐药', 'n');
