INSERT INTO `main_ori` VALUES ('PMID:25088940|NCCN Guideline: Colon Cancer <2.2019>', '.', 'guideline:NCCN', '1', 'FOLFIRI + cetuximab', 'FOLFIRI + cetuximab', 'FOLFIRI + cetuximab', 'FOLFIRI + 西妥昔单抗', 'FOLFIRI + cetuximab|FOLFIRI + 西妥昔单抗', '4', 'metastatic adenocarcinoma of the colon or rectum', ' metastatic colorectal cancer ', '结直肠癌', 'na', 'resistance', 'EVI_001549', 'KRAS:nonsynonymous SNV:.:.:act', 'KRAS', 'nonsynonymous SNV', 'None_pos', '.', '.', 'act', '.', '在一项开放标签的随机III期临床试验中，592例晚期结直肠癌患者呗随机分为两组，分别接受FOLFIRI联合西妥昔单抗或贝伐单抗治疗。对于携带任何KRAS、NRAS突变的患者，接受西妥昔单抗联合疗法的无进展生存期显著短于贝伐单抗联合疗法（6.1 vs 12.2个月；P=0.004）。而对于RAS的野生型患者，接受两种疗法的无进展生存期没有显著差异（10.4 vs 10.2个月，P=0.54）。这些结果表明，西妥昔单抗不能使携带RAS突变的患者生存获益，且毒副作用较大。', 's', '耐药', 'n');
