INSERT INTO `main_ori` VALUES ('PMID:30449794|PMID:20979472|NCCN Guideline: Soft Tissue Sarcoma <4.2019>', 'NCT00585195', 'guideline:NCCN', '1', 'ceritinib', 'ceritinib', 'ceritinib', '塞瑞替尼', 'ceritinib|塞瑞替尼', '4', 'Inflammatory Myofibroblastic Tumor', '.', '炎性肌纤维母细胞瘤', '[ALK:FUS]', 'sensitivity', 'EVI_001584', 'ALK:FUS:.:.-ALK:.', 'ALK', 'FUS', 'FUS', '.', '.-ALK', '.', '.', '一名37岁炎性肌纤维母细胞瘤的女性患者，手术完全切除后肿瘤复发。基因检测发现其携带ALK融合基因突变，使用crizotinib治疗导致后获得完全缓解。在另一项临床试验中，一名携带ALK基因融合的炎性肌纤维母细胞瘤患者使用crizotinib治疗后获得部分缓解。NCCN指南目前也推荐携带ALK基因突变炎性肌纤维母细胞瘤（Inflammatory myofibroblastic tumor）的患者可以考虑使用crizotinb或ceritinib进行治疗。', 's', '敏感', 'n');
