INSERT INTO `main_ori` VALUES ('PMID:25182956', '.', 'case report', '4', 'imatinib', 'imatinib', 'imatinib', '伊马替尼', 'imatinib|伊马替尼', '4', '.', 'gastrointestinal stromal tumor', '胃肠间质瘤', 'na', 'resistance', 'EVI_001619', 'BRAF:nonsynonymous SNV:exon15:p.V600.:.', 'BRAF', 'nonsynonymous SNV', 'Partial_pos', 'exon15', 'p.V600.', '.', '.', '一名携带KIT V559D突变的GIST患者在使用imatinib 18个月后发生耐药。随后的基因检测发现该患者在BRAF基因上出现了V600E突变。该研究提示BRAF基因突变可能是导致GIST患者对imatinib耐药的原因。', 's', '可能耐药', 'n');
