INSERT INTO `main_ori` VALUES ('PMID:31587882', 'NCT03046992', 'clinical trial - phase2', '4', 'Lazertinib', 'Lazertinib', 'lazertinib', 'lazertinib', 'lazertinib|lazertinib', '2', 'Non-Small-Cell Lung Cancer', 'Non-Small-Cell Lung Cancer', '肺癌', 'na', 'sensitivity', 'EVI_001629', 'EGFR:nonsynonymous SNV:exon20:p.T790M:', 'EGFR', 'nonsynonymous SNV', 'Exact_pos', 'exon20', 'p.T790M', '.', '.', '首个评估lazertinib疗效的I-II期研究在韩国14家中心开展。研究的主要终点是安全性和耐受性，次要终点包括ORR等。该研究于今年11月份发表于《柳叶刀·肿瘤学》。结果显示，在127例可评估患者中（携带EGFR敏感突变和T790M耐药突变），独立评审委员会评估的ORR为54%，52%患者部分缓解，2%完全缓解，32%患者获得疾病稳定，87%的患者达到疾病控制，研究者评估的ORR为60%，疾病控制率为90%。', 's', '可能敏感', 'n');
