INSERT INTO `main_ori` VALUES ('NCCN Guideline: Non-Small Cell Lung Cancer (NSCLC) <2.2020>|PMID:19667264', '.', 'guideline:NCCN', '1', 'erlotinib', 'erlotinib', 'erlotinib', '厄洛替尼', 'erlotinib|厄洛替尼', '4', 'non-small cell lung cancer', 'non-small cell lung cancer', '肺癌', 'na', 'resistance', 'EVI_001661', 'ALK:FUS:.:.-ALK:.', 'ALK', 'FUS', 'FUS', '.', '.-ALK', '.', '.', '约5%的NSCLC患者携带ALK基因重排。ALK重排的患者对EGFR TKI具有耐药性，但与EGFR突变的患者具有相似的临床特征（即腺癌组织学，轻度或从不吸烟）。区别携带EML4-ALK和EGFR突变的癌症类型具有重要的治疗意义 。EGFR突变的癌细胞对EGFR TKIs敏感，而EML4-ALK与EGFR TKIs耐药性密切相关。一项临床研究中，19例对厄洛替尼或吉非替尼有临床响应的患者中，16例（占84%）具有激活的EGFR突变，而没有任何患者携带EML4-ALK融合。相反，在34例对EGFR TKI难治的患者中，有10例（29%）患者携带EML4-ALK融合。这些发现与临床前研究一致，含有EML4-ALK融合突变的NSCLC细胞系H3122对厄洛替尼耐药。', 's', '耐药', 'n');
