INSERT INTO `main_ori` VALUES ('DOI:10.1016/j.jtho.2019.08.1375', 'NCT03130881', 'clinical trial - phase1', '4', '.', '.', 'PLB1003', 'PLB1003', 'PLB1003|PLB1003', '1', 'non small-cell lung cancer', 'non small-cell lung cancer', '肺癌', '[ALK:FUS]', 'sensitivity', 'EVI_001697', 'ALK:FUS:.:.-ALK:', 'ALK', 'FUS', 'FUS', '.', '.-ALK', '.', 'In a Phase Ia clinical trial, PLB1003 treatment resulted in a disease control rate of 86% with a 71% partial response (10/14) and 14% stable disease (2/14) in ALK rearranged non-small cell lung cancer patients with prior therapy (Journal of Thoracic Oncology, Volume 14, Issue 10, S651).', '在I期临床试验中，携带ALK基因融合的非小细胞肺癌患者使用PLB1003治疗后，71%(10/14)获得部分缓解，14%(2/14)患者疾病稳定。', 'F', '可能敏感', 'n');
INSERT INTO `main_ori` VALUES ('J Clin Oncol 33, 2015 (suppl; abstr 8063)|J Clin Oncol 32, 2014 (suppl; abstr e19005)', 'NCT02048488', 'clinical trial - phase1', '4', '.', '.', 'belizatinib', 'belizatinib', 'belizatinib|belizatinib', '1', 'Advanced Solid Tumor', 'Advanced Solid Tumor', '非特定癌症', '[ALK:FUS]', 'sensitivity', 'EVI_001698', 'ALK:FUS:.:.-ALK:', 'ALK', 'FUS', 'FUS', '.', '.-ALK', '.', 'In a Phase I trial, Belizatinib (TSR-011) treatment resulted in a response in 100% (3/3) of patients with ALK positive advanced solid tumors when administered at higher doses, and stable disease for 7 months or longer in 56% (5/9) of patients at lower dose (J Clin Oncol 33, 2015 (suppl; abstr 8063)；NCT02048488.).', '一项I期临床试验中，ALK基因阳性的实体瘤患者使用高剂量Belizatinib治疗后，100%(3/3)患者有治疗响应；使用低剂量belizatinib治疗后56%(5/9)患者疾病稳定超过7个月。', 'F', '可能敏感', 'n');
INSERT INTO `main_ori` VALUES ('PMID:27601542', 'NCT01399918', 'clinical trial - phase2', '4', '.', '.', 'everolimus + bevacizumab', '依维莫司 + 贝伐珠单抗', 'everolimus + bevacizumab|依维莫司 + 贝伐珠单抗', '2', 'renal cell carcinom', 'renal cell carcinom', '肾癌', 'na', 'sensitivity', 'EVI_001699', 'ARID1A:nonsynonymous SNV:.:.:inact', 'ARID1A', 'nonsynonymous SNV', 'None_pos', '.', '.', 'inact', 'In a Phase II trial, 100% (5/5) of renal cell carcinoma patients harboring ARID1A mutations achieved the primary endpoint of 6 month PFS when treated with the combination therapy Afinitor (everolimus) and Avastin (bevacizumab) (PMID:27601542).', '一项II期临床实验中，携带ARID1A基因突变的肾癌患者使用Afinitor (everolimus) + Avastin (bevacizumab)联合治疗后获得了6个月的PFS。', 's', '可能敏感', 'n');
INSERT INTO `main_ori` VALUES ('PMID:29103871', 'NCT01924533', 'clinical trial - phase3', '4', '.', '.', 'paclitaxel + olaparib', '紫杉醇 + 奥拉帕利', 'paclitaxel + olaparib|紫杉醇 + 奥拉帕利', '3', 'gastric cancer', 'gastric cancer', '胃癌', 'na', 'resistance', 'EVI_001701', 'ATM:nonsynonymous SNV:.:.:inact', 'ATM', 'nonsynonymous SNV', 'None_pos', '.', '.', 'inact', 'In a Phase III trial (GOLD), addition of Lynparza (olaparib) to Taxol (paclitaxel) did not significantly improve overall survival (12.0 vs 10.0 months, HR=0.73, p=0.25) compared to Taxol (paclitaxel) alone in Asian patients with ATM-negative gastric cancer (PMID: 29103871; NCT01924533).', '一项临床III期试验中，相比paclitaxel单药，paclitaxel联合olaparib不能改善ATM缺失胃癌患者的总体生存期（12.0 vs 10.0 months, HR=0.73, p=0.25）。', 's', '可能耐药', 'n');
INSERT INTO `main_ori` VALUES ('PMID:29320312', 'NCT02091141', 'clinical trial - phase2', '4', 'trastuzumab + pertuzumab', 'trastuzumab + pertuzumab', 'trastuzumab + pertuzumab', '曲妥珠单抗 + 帕妥珠单抗', 'trastuzumab + pertuzumab|曲妥珠单抗 + 帕妥珠单抗', '2', 'non-small cell lung cancer', 'non-small cell lung cancer', '肺癌', 'na', 'sensitivity', 'EVI_001702', 'ERBB2:CNV:.:.:gain', 'ERBB2', 'CNV', 'CNV', '.', '.', 'gain', 'In a Phase II trial (MyPathway), Herceptin (trastuzumab) and Perjeta (pertuzumab) combination treatment resulted in objective response in 13% (2/16, all partial response) and stable disease lasting over 120 days in 13% (2/16) of patients with non-small cell lung cancer harboring ERBB2 (HER2) amplification or overexpression (PMID: 29320312; NCT02091141).', '在一项II期临床试验（MyPathway）中，携带ERBB2基因扩增或过表达的非小细胞肺癌患者使用Herceptin (trastuzumab)和Perjeta (pertuzumab) 联合治疗，客观缓解率为13%（2/16），病情稳定超过120天的患者为13%（2/16）。', 'A', '可能敏感', 'n');
INSERT INTO `main_ori` VALUES ('DOI:10.1016/j.jtho.2017.09.061', '.', 'case report', '4', 'osimertinib', 'osimertinib', 'osimertinib', '奥希替尼', 'osimertinib|奥希替尼', '4', '.', '.', '肺癌', 'na', 'resistance', 'EVI_001703', 'RET:FUS:.:.-RET:.', 'RET', 'FUS', 'FUS', '.', '.-RET', '.', '.', '一名EGFR外显子19缺失的肺癌患者最初对EGFR TKI治疗有反应，但是因为T790M突变产生了获得性耐药。随后，该患者采用了rociletinib治疗，然后采用osimertinib作为CNS进展的标准治疗方法，但疾病依然发生进展。除了EGFR del 19和T790M突变外，该患者还发现了一种CCDC6-RET融合基因突变，可能是其对osimertinib耐药的原因。', 'F', '可能耐药', 'n');
INSERT INTO `main_ori` VALUES ('Druglabel:olaparib_Revised:12/2019', 'NCT02000622', 'guideline:FDA', '1', 'olaparib', 'olaparib', 'olaparib', '奥拉帕利', 'olaparib|奥拉帕利', '4', 'breast cancer', 'breast cancer', '乳腺癌(HER2阴性)', '[NA;NA;HER2(-)]', 'sensitivity', 'EVI_001704', 'BRCA1:nonsynonymous SNV:::inact', 'BRCA1', 'nonsynonymous SNV', 'None_pos', '.', '.', 'inact', '.', '2018年1月12日FDA批准了阿斯利康Lynparza（olaparib，奥拉帕利）的新适应症，用于治疗携带BRCA胚系突变（gBRCA）的HER2阴性转移性乳腺癌。这是PARP抑制剂首次被批准用于治疗乳腺癌。该适应症获批是基于一项Ⅲ期临床试验(NCT02000622)，入组患者均是未超过二线以上化疗的晚期乳腺癌，经检测后确定伴有gBRCA突变。一共302例患者，按照2:1的比例分配到奥拉帕利组（300mg，口服，2次/日）或者是标准化疗组（三种方案：卡培他滨、艾立布林和长春瑞滨单药）。结果发现奥拉帕利组的有效率高达59.9%，比化疗高出1倍（28.8%）；无疾病进展时间（PFS）延长了接近3个月，奥拉帕利组是7个月，而化疗患者仅为4.2个月。在不良反应方面，奥拉帕利也明显低于化疗组。3级以上的毒性主要为血液学毒性：贫血（16.1%）、中性粒细胞减少（9.3%）和白细胞计数减少（3.4%）；3级以上的非血液学毒性发生率都在3%以下，主要表现在疲乏（2.9%）、头痛（1.0%）和谷丙转氨酶（1.5%）、谷草转氨酶（2.4%）的异常升高。', 'G;S', '敏感', 'n');
